mapper.insert:
insert into tb_post (

) values (
      #{id}
    , #{title}
    , #{content}
    , #{writer}
    , 0
    , #{noticeYn}
    , false
    , now()
    , null
)

mapper.update:
update tb_post
set
      title = #{title}
    , content = #{content}
    , writer = #{writer}
    , notice_yn = #{noticeYn}
    , modified_date = now()
where
    id = #{id}
